import axios from "axios";

export const POST_DATA = "post_data";
export const FETCH_DATA = "fetch_data";
const ROOT_URL = `http://192.168.0.204:3001/api`;

export function loginPostData(values, callback) {
  const request = axios
    .post(`${ROOT_URL}/login`, values)
    .then((response) => {
      callback(response);
      return response})
    .catch(error => {
      alert(error);
    });
  return {
    type: POST_DATA,
    payload: request
  };
}
export function signupPostData(values, callback) {
  console.log("SD DATA : ", values)
  const request = axios
    .post(`${ROOT_URL}/signup`, values)
    .then((response) => {
      callback(response);
      return response;
    })
    .catch(error => {
      alert(error);
    });
  return {
    type: POST_DATA,
    payload: request
  };
}

